MCS.mcs_Service_Activity_OnSave.ConvertDateToCIDFormat = function (date) {
    //2019-06-26
    var d = new Date(date),
        month = '' + (d.getMonth() + 1),
        day = '' + d.getDate(),
        year = d.getFullYear();

    if (month.length < 2)
        month = '0' + month;

    if (day.length < 2)
        day = '0' + day;

    return year + '-' + month + '-' + day;// + 't04:00:00.000z';
}

